pragma solidity ^0.8.20;

// BondingMath.sol
import "./libraries/BondingMath.sol";
import "./libraries/UniswapPoolCreator.sol";
import "./interfaces/IBondingCurve.sol";
import "./interfaces/IFactory.sol";
import "./interfaces/ILock.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";


interface IToken is IERC20 {
    function TOTAL_SUPPLY() external returns (uint256);
}

// BondingCurve.sol
contract BondingCurve is
    IBondingCurve,
    Initializable,
    Ownable2StepUpgradeable,
    ReentrancyGuardUpgradeable,
    IERC721Receiver
{
    using BondingMath for uint256;
    using UniswapPoolCreator for *;
    using Address for address payable;
    using SafeERC20 for IERC20;
    using SafeERC20 for IToken;

    // Constants
    IFactory.BondingCurveSettings private settings;

    // State variables
    IToken public token;
    address public lockContract;
    address public uniswapPool;
    uint256 public lpTokenId;
    bool public isFinalized;

    enum Phase {
        PreBonding,
        Bonding,
        BondingCompleted,
        Finalized
    }

    Phase public currentPhase;

    uint256 public totalPreBondingContributions;
    uint256 public ethReserve;
    uint256 public tokenReserve;
    uint256 public preBondingTokens;
    uint256 public totalETHCollected;
    uint256 public accumulatedFees;

    mapping(address => uint256) public contributions;
    mapping(address => bool) public tokenLocks;
    mapping(address => uint256) public tokenAllocations;

    modifier onlyPhase(Phase phase) {
        if (currentPhase != phase) revert InvalidPhase();
        _;
    }
    modifier onlyBondingOrCompleted() {
        if (currentPhase != Phase.Bonding && currentPhase != Phase.BondingCompleted)
            revert InvalidPhase();
        _;
    }

    function initialize(
        address token_,
        address lock_,
        address owner_,
        IFactory.BondingCurveSettings calldata settings_
    ) external initializer {
        if (token_ == address(0) || lock_ == address(0) || owner_ == address(0))
            revert ZeroAddress();

        __Ownable_init(owner_);
        __ReentrancyGuard_init();

        token = IToken(token_);
        lockContract = lock_;
        settings = settings_;
        currentPhase = Phase.PreBonding;
    }

    function getBondingCurveSettings()
        external
        view
        returns (IFactory.BondingCurveSettings memory)
    {
        return settings;
    }

    function contributePreBonding()
        external
        payable
        nonReentrant
        onlyPhase(Phase.PreBonding)
    {
        if (msg.value < settings.minContribution) revert ContributionTooLow();

        uint256 newTotal = totalPreBondingContributions + msg.value;
        if (newTotal > settings.preBondingTarget)
            revert PreBondingTargetReached();

        uint256 tokensOut = BondingMath.calculateTokensForETH(
            settings.virtualEth, // virtual
            token.balanceOf(address(this)), // total supply (10B)
            msg.value // contribution amount
        );
        contributions[msg.sender] += msg.value;
        tokenAllocations[msg.sender] += tokensOut;
        totalPreBondingContributions += msg.value;
        preBondingTokens += tokensOut;
        tokenLocks[msg.sender] = true;
        emit PreBondingContribution(msg.sender, msg.value, tokensOut);
        if (totalPreBondingContributions >= settings.preBondingTarget) {
            currentPhase = Phase.Bonding;
            // Now ethReserve includes both virtual and real ETH
            ethReserve = settings.virtualEth + totalPreBondingContributions;
            totalETHCollected = totalPreBondingContributions;
            // tokenReserve is reduced by allocated tokens
            tokenReserve = token.balanceOf(address(this)) - preBondingTokens;
        }
    }

    function buyTokens(
        uint256 minTokens
    )
        external
        payable
        nonReentrant
        onlyPhase(Phase.Bonding)
        returns (uint256 tokensToReceive)
    {
        if (msg.value < settings.minContribution) revert ContributionTooLow();
        if (totalETHCollected >= settings.bondingTarget)
            revert BondingTargetReached();

        tokensToReceive = BondingMath.calculateTokensForETH(
            ethReserve,
            tokenReserve,
            msg.value
        );

        if (tokensToReceive < minTokens) revert SlippageExceeded();
        if (tokenReserve < tokensToReceive) revert InsufficientTokens();

        ethReserve += msg.value;
        tokenReserve -= tokensToReceive;
        totalETHCollected += msg.value;
        token.safeTransfer(msg.sender, tokensToReceive);
        emit TokensPurchased(msg.sender, msg.value, tokensToReceive);
        if (totalETHCollected >= settings.bondingTarget) {
            currentPhase = Phase.BondingCompleted;
        }
    }

    function sellTokens(
        uint256 tokenAmount,
        uint256 minETH
    )
        external
        nonReentrant
        onlyBondingOrCompleted()
        returns (uint256 ethToReceive, uint256 fee)
    {
        if (tokenAmount == 0) revert InsufficientTokens();
        (ethToReceive, fee) = BondingMath.calculateETHForTokens(
            ethReserve,
            tokenReserve,
            tokenAmount,
            settings.sellFee
        );
        if (ethToReceive < minETH) revert SlippageExceeded();
        uint256 availableETH = ethReserve - settings.virtualEth;
        if (availableETH < ethToReceive + fee) revert InsufficientETH();
        ethReserve -= (ethToReceive + fee);
        tokenReserve += tokenAmount;
        token.safeTransferFrom(msg.sender, address(this), tokenAmount);
        payable(msg.sender).sendValue(ethToReceive);
        payable(settings.feeTo).sendValue(fee);
        emit TokensSold(msg.sender, tokenAmount, ethToReceive, fee);
    }

    function finalizeCurve() external nonReentrant {
        if (totalETHCollected < settings.bondingTarget)
            revert CannotFinalizeYet();
        if (isFinalized) revert AlreadyFinalized();

        UniswapPoolCreator.PoolParams memory poolParams = UniswapPoolCreator
            .PoolParams({
                factory: settings.uniswapV3Factory,
                token: address(token),
                weth: settings.weth,
                fee: settings.poolFee,
                ethReserve: ethReserve,
                tokenReserve: tokenReserve
            });

        address pool = UniswapPoolCreator.createAndInitializePool(poolParams);
        uint256 ethLiquidity = address(this).balance;
        uint256 tokenLiquidity = (ethLiquidity * tokenReserve) / ethReserve;

        token.safeIncreaseAllowance(settings.positionManager, tokenLiquidity);

        UniswapPoolCreator.PositionParams memory posParams = UniswapPoolCreator
            .PositionParams({
                positionManager: settings.positionManager,
                token: address(token),
                weth: settings.weth,
                fee: settings.poolFee,
                ethAmount: ethLiquidity,
                tokenAmount: tokenLiquidity
            });
        uint256 tokenId = UniswapPoolCreator.createLPPosition(posParams);
        // Approve lock contract to transfer the NFT
        INonfungiblePositionManager(settings.positionManager).approve(
            lockContract,
            tokenId
        );
        ILock(lockContract).lockNFT(tokenId, owner());
        uniswapPool = pool;
        lpTokenId = tokenId;
        isFinalized = true;
        currentPhase = Phase.Finalized;
        emit CurveFinalized(pool, tokenId);
    }

    receive() external payable {}

    /**
     * @notice Withdraw allocated tokens after curve finalization
     * @dev Can only be called after curve is finalized and only by addresses with allocations
     * @param recipient Address to receive the tokens
     */
    function withdrawTokenAllocation(address recipient) external nonReentrant {
        if (!isFinalized) revert CannotFinalizeYet();
        if (recipient == address(0)) revert ZeroAddress();
        if (!tokenLocks[msg.sender]) revert TokensNotLocked();
        uint256 allocation = tokenAllocations[msg.sender];
        if (allocation == 0) revert InsufficientTokens();
        // Reset state before transfer to prevent reentrancy
        tokenAllocations[msg.sender] = 0;
        tokenLocks[msg.sender] = false;
        // Transfer tokens to the specified recipient
        token.safeTransfer(recipient, allocation);

        emit TokensUnlocked(recipient);
    }

    /**
     * @notice Implements IERC721Receiver
     */
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure returns (bytes4) {
        return this.onERC721Received.selector;
    }
}","keccak256":"0xcc0e1b282efabc5ce8e0d8a10a4e08830bdca844b8cf4f71c160fdfcfd9291b5","license":"MIT"},"contracts/interfaces/IBondingCurve.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import "./IFactory.sol";

/**
 * @title IBondingCurve
 * @notice Interface for the bonding curve contract that manages token distribution and liquidity
 * @dev Implements constant product AMM formula with pre-bonding and bonding phases
 */
interface IBondingCurve {
    // Custom Errors
    error ZeroAddress();
    error InitializationFailed();
    error PreBondingTargetReached();
    error BondingTargetReached();
    error ContributionTooLow();
    error InvalidPhase();
    error TokensLocked();
    error TokensNotLocked();
    error InsufficientTokens();
    error InsufficientETH();
    error SlippageExceeded();
    error TransferFailed();
    error CannotFinalizeYet();
    error AlreadyFinalized();
    error NoFeesToWithdraw();

    // Events
    event PreBondingContribution(
        address indexed contributor,
        uint256 amount,
        uint256 tokenAmount
    );

    event PreBondingCompleted(uint256 totalContributed, uint256 totalTokens);

    event TokensUnlocked(address indexed account);

    event TokensPurchased(
        address indexed buyer,
        uint256 ethAmount,
        uint256 tokenAmount
    );

    event TokensSold(
        address indexed seller,
        uint256 tokenAmount,
        uint256 ethAmount,
        uint256 fee
    );

    event CurveFinalized(
        address indexed uniswapPool,
        uint256 indexed lpTokenId
    );

    event FeesWithdrawn(uint256 amount);

    // State-Changing Functions
    /**
     * @notice Initialize the bonding curve contract
     * @param token_ Address of the ERC20 token
     * @param lock_ Address of the LP NFT lock contract
     * @param owner_ Address that will own the contract
     */
    // Update initialize function
    function initialize(
        address token_,
        address lock_,
        address owner_,
        IFactory.BondingCurveSettings calldata settings_
    ) external;

    /**
     * @notice Contribute ETH during pre-bonding phase
     */
    function contributePreBonding() external payable;

    /**
     * @notice Buy tokens during bonding phase
     * @param minTokens Minimum tokens to receive (slippage protection)
     */
    function buyTokens(
        uint256 minTokens
    ) external payable returns (uint256 tokensToReceive);

    /**
     * @notice Sell tokens during bonding phase
     * @param tokenAmount Amount of tokens to sell
     * @param minETH Minimum ETH to receive (slippage protection)
     */
    function sellTokens(
        uint256 tokenAmount,
        uint256 minETH
    ) external returns (uint256 ethToReceive, uint256 fee);

    /**
     * @notice Finalize the curve and create Uniswap pool
     * @dev Only callable when bonding target is reached
     */
    function finalizeCurve() external;
}
","keccak256":"0x3cb9ab3d50d71eb16676f1540979c45b610cfb46a7c773daf865dc974ef0a88b","license":"MIT"},"contracts/interfaces/IFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IFactory
 * @notice Interface for the factory contract that deploys token and bonding curve instances
 * @dev Factory is deployed via minimal proxy pattern and implements Initializable
 */
interface IFactory {
    // Custom Errors
    /// @notice Thrown when an address parameter is zero
    error ZeroAddress();

    /// @notice Thrown when initialization fails
    error InitializationFailed();

    /// @notice Thrown when caller is not authorized
    error Unauthorized();

    /// @notice Thrown when deployment fee is insufficient
    /// @param provided The amount of ETH provided
    /// @param required The required deployment fee
    error InsufficientDeploymentFee(uint256 provided, uint256 required);

    /// @notice Thrown when deployment parameters are invalid
    error InvalidDeploymentParameters();

    /// @notice Thrown when deployment minting fails
    error TokenMintingFailed();

    /// @notice Thrown when deployment extra fees refund fails
    error RefundFailed();
    error NoFeesToWithdraw();
    error FeeWithdrawalFailed();

    // Events
    /**
     * @notice Emitted when a new bonding curve system is deployed
     * @param bondingCurveAddress The address of the deployed bonding curve
     * @param tokenAddress The address of the deployed token
     * @param owner The address that will own both contracts
     * @param name Token name
     * @param symbol Token symbol
     */
    event BondingCurveSystemDeployed(
        address indexed bondingCurveAddress,
        address indexed tokenAddress,
        address indexed owner,
        string name,
        string symbol
    );

    /**
     * @notice Emitted when deployment fee is updated
     * @param oldFee Previous fee amount
     * @param newFee New fee amount
     */
    event DeploymentFeeUpdated(uint256 oldFee, uint256 newFee);

    /**
     * @notice Emitted when fees are withdrawn
     * @param recipient Address receiving the fees
     * @param amount Amount withdrawn
     */
    event FeesWithdrawn(address indexed recipient, uint256 amount);

    // Add new events
    event BondingCurveSettingsUpdated(
        uint256 virtualEth,
        uint256 preBondingTarget,
        uint256 bondingTarget,
        uint256 minContribution,
        uint24 poolFee
    );

    struct BondingCurveSettings {
        uint256 virtualEth;
        uint256 preBondingTarget;
        uint256 bondingTarget;
        uint256 minContribution;
        uint24 poolFee;
        uint24 sellFee;
        address uniswapV3Factory;
        address positionManager;
        address weth;
        address feeTo;
    }

    // Add new functions
    function getBondingCurveSettings()
        external
        view
        returns (BondingCurveSettings memory);

    function updateBondingCurveSettings(
        BondingCurveSettings calldata newSettings
    ) external;

    // View Functions
    /**
     * @notice Get the current deployment fee
     * @return Current fee amount in wei
     */
    function getDeploymentFee() external view returns (uint256);

    /**
     * @notice Get the token implementation address
     * @return Address of the token implementation contract
     */
    function getTokenImplementation() external view returns (address);

    /**
     * @notice Get the bonding curve implementation address
     * @return Address of the bonding curve implementation contract
     */
    function getBondingCurveImplementation() external view returns (address);

    /**
     * @notice Get the LP NFT lock contract address
     * @return Address of the LP NFT lock contract
     */
    function getLockContract() external view returns (address);

    // State-Changing Functions
    /**
     * @notice Initialize the factory contract
     * @param owner Address that will own the factory
     * @param tokenImpl Address of the token implementation
     * @param bondingCurveImpl Address of the bonding curve implementation
     * @param lockContract Address of the LP NFT lock contract
     */
    // Update initialize function
    function initialize(
        uint256 factoryFees,
        address owner,
        address tokenImpl,
        address bondingCurveImpl,
        address lockContract,
        BondingCurveSettings calldata settings
    ) external;

    /**
     * @notice Deploy a complete bonding curve system (token + bonding curve)
     * @param name Token name
     * @param symbol Token symbol
     * @return tokenAddress Address of the deployed token contract
     * @return bondingCurveAddress Address of the deployed bonding curve
     */
    function deployBondingCurveSystem(
        string calldata name,
        string calldata symbol
    )
        external
        payable
        returns (address tokenAddress, address bondingCurveAddress);

    /**
     * @notice Update the deployment fee
     * @param newFee New fee amount in wei
     */
    function updateDeploymentFee(uint256 newFee) external;

    /**
     * @notice Withdraw collected fees
     * @param recipient Address to receive the fees
     */
    function withdrawFees(address recipient) external;
}
","keccak256":"0x241cb344383f28f7ff9a72cb71bfff6dad2fecedaaa1822aa15962a5351f873b","license":"MIT"},"contracts/interfaces/ILock.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ILock
 * @notice Interface for the LP NFT lock contract
 * @dev Handles locking of Uniswap V3 LP NFTs and fee management
 */
interface ILock {
    // Custom Errors
    /// @notice Thrown when NFT is already locked
    error NFTAlreadyLocked();

    /// @notice Thrown when NFT is not locked
    error NFTNotLocked();

    /// @notice Thrown when caller is not the NFT owner
    error NotNFTOwner();

    /// @notice Thrown when lock period has not ended
    error LockPeriodNotEnded();

    // Events
    /**
     * @notice Emitted when an NFT is locked
     * @param owner Address of the NFT owner
     * @param tokenId ID of the locked NFT
     */
    event NFTLocked(address indexed owner, uint256 indexed tokenId);

    /**
     * @notice Emitted when an NFT is unlocked
     * @param owner Address of the NFT owner
     * @param tokenId ID of the unlocked NFT
     */
    event NFTUnlocked(
        address indexed owner,
        uint256 indexed tokenId,
        address to
    );

    /**
     * @notice Emitted when fees are claimed
     * @param owner Address claiming the fees
     * @param tokenId NFT position ID
     * @param amount0 Amount of token0 fees claimed
     * @param amount1 Amount of token1 fees claimed
     */
    event FeesClaimed(
        address indexed owner,
        uint256 indexed tokenId,
        uint256 amount0,
        uint256 amount1
    );

    // Functions
    function initialize() external;

    function lockNFT(uint256 tokenId, address owner) external;

    function unlockNFT(uint256 tokenId, address to) external;

    function claimFees(
        uint256 tokenId
    ) external returns (uint256 amount0, uint256 amount1);

    function checkAvailableFees(
        uint256 tokenId
    ) external view returns (uint256 amount0, uint256 amount1);

    function getNFTsByOwner(
        address owner
    ) external view returns (uint256[] memory);

    function isNFTLocked(uint256 tokenId) external view returns (bool);

    function getRemainingLockTime(
        uint256 tokenId
    ) external view returns (uint256);
}
