    // Structs
    struct LockedNFT {
        address owner;
        uint256 tokenId;
        bool isLocked;
        uint256 lockedAt;
    }

    // State variables
    INonfungiblePositionManager public positionManager;
    mapping(uint256 => LockedNFT) public lockedNFTs;
    mapping(address => uint256[]) public ownerNFTs;

    // Constants
    uint256 public constant LOCK_DURATION = 3650 days; // ten years.
