// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

library LibPrize {
    bytes32 private constant PRIZE_TYPE =
        keccak256("PRIZE(address to,uint256 amount,uint256 uuid)");
    struct PRIZE {
        address to;
        uint256 amount;
        uint256 uuid;
    }

